	/**
	 * 通过java 调用js引擎实现计算公式
	 * 通过字符串公式，和参数，计算出结果
	 * @param formula 计算公式
	 * @param param 参数集合
	 * @return
	 * @throws ScriptException 
	 * @throws FileNotFoundException 
	 */
	public static Object calculateResultByFormula (String formula,Map<String,Object> param) throws ScriptException {
		ScriptEngineManager manager = new ScriptEngineManager();
		ScriptEngine scriptEngine = manager.getEngineByName("js");
//		scriptEngine.eval(new InputStreamReader(new FileInputStream(""))); //导入js 库
		for (String key : param.keySet()) {
			formula = formula.replaceAll(key, param.get(key).toString());
		}
        //此时 formula="(20.1+11.1)*(20.1-11.1)"
		Object result = scriptEngine.eval(formula); // 运行js脚本
		
		return result;
	}
	
	/**
	 * 执行js定义的function，获取返回值
	 * @param fun
	 * @param args
	 * @return
	 * @throws NoSuchMethodException
	 * @throws ScriptException
	 * @author yansheng
	 */
	public static Object invokeJsFun(String fun,String funName,Object... args) throws NoSuchMethodException, ScriptException{
		
	   ScriptEngineManager manager = new ScriptEngineManager();
	   ScriptEngine engine = manager.getEngineByName("js");
	   engine.eval(fun);
	   Invocable invocable = (Invocable) engine;
	   Object result = (Object) invocable.invokeFunction(funName, args);
		
	   return result;
	}
	
	
	
	
	
	
	
	
	
